}
HeaderLogo.Nav = props => {
  return <nav {...props} />
}
HeaderLogo.Link = props => {
  return (
    <Link
      sx={{
        variant: 'styles.navLink',
        p: 2
      }}
      {...props}
    />
  )
}

applyPropertyControls(HeaderLogo, {
  justifyContent: {
    type: ControlType.Enum,
    defaultValue: 'right',
    options: ['space-between', 'start', 'space-evenly']
  },
  sx: {
    type: ControlType.Style
  }
})

const linkControls = {
  children: {
    title: 'Text',
    type: ControlType.String,
    required: true
  },
  to: {
    title: 'URL',
    type: ControlType.String,
    defaultValue: '#!',
    required: true
  },
  sx: {
    type: ControlType.Style
  }
}

applyPropertyControls(HeaderLogo.Logo, linkControls)
applyPropertyControls(HeaderLogo.Link, linkControls)

HeaderLogo.usage = `
  <HeaderLogo>
    <HeaderLogo.Logo to="/" />
    <HeaderLogo.Nav>
      <HeaderLogo.Link to="/about">О нас</HeaderLogo.Link>
      <HeaderLogo.Link to="/blog">Блог</HeaderLogo.Link>
      <HeaderLogo.Link to="/contact">Контакты</HeaderLogo.Link>
    </HeaderLogo.Nav>
  </HeaderLogo>
`

export default HeaderLogo

